package aula;

public class Temporizador {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 24; i >= 0; i--) {
			for (int j = 24; j >= 0; j--) {
				for (int K = 24; K >= 0; K--) {
					
					System.out.println(i+":"+j+":"+K);
					
				}
			}
		}
	}

}
